// @flow

import pathToRegexp from "path-to-regexp";

/** @ignore */
interface Route {
  value: any;
  params: any;
  childPath?: ?string;
}

/** @ignore */
class RouteEntry {
  /** @ignore */
  params: any;
  /** @ignore */
  id: string;
  /** @ignore */
  regexp: RegExp;
  /** @ignore */
  toPath: any => string;
  /** @ignore */
  keys = [];
  /** @ignore */
  isStar: boolean;
  /** @ignore */
  isPrefix: boolean;
  /** @ignore */
  childMatcher: RouteMatcher;
  /** @ignore */
  constructor(routeSetting: {
    params: any,
    id: string,
    path: string,
    children: any,
    isPrefix: boolean,
  }) {
    this.params = routeSetting.params;
    this.id = routeSetting.id;
    const pathOptions = Object.assign(
      ({ end: !routeSetting.isPrefix && !routeSetting.children }: any),
      routeSetting
    );
    this.isPrefix = !pathOptions.end;
    this.regexp = pathToRegexp(routeSetting.path, this.keys, pathOptions);
    this.toPath = pathToRegexp.compile(routeSetting.path);
    this.isStar = routeSetting.path === "*";
    if (routeSetting.children) {
      this.childMatcher = new RouteMatcher(routeSetting.children);
    }
  }
  /** @ignore */
  match(path: string): ?Route {
    if (path === null) {
      return null;
    }
    if (this.isStar) {
      return {
        value: {
          "0": this.decodeParam(path),
          id: this.id,
        },
        params: this.params,
      };
    }
    const match = this.regexp.exec(path);
    if (!match) {
      return null;
    }
    const value = { id: this.id };
    for (let i = 1; i < match.length; i += 1) {
      const key = this.keys[i - 1];
      const val = this.decodeParam(match[i]);
      if (
        val !== undefined ||
        !Object.prototype.hasOwnProperty.call(value, key.name)
      ) {
        if (key.repeat && val) {
          value[key.name] = val.split(key.delimiter);
        } else {
          value[key.name] = val;
        }
      }
    }
    let childPath: ?string = null;
    if (this.isPrefix) {
      childPath = path.slice(match[0].length);
    }
    return {
      value,
      params: this.params,
      childPath: childPath,
    };
  }
  /** @ignore */
  decodeParam(val: any) {
    if (typeof val !== "string" || val.length === 0) {
      return val;
    }
    try {
      return decodeURIComponent(val);
    } catch (err) {
      if (err instanceof URIError) {
        err.message = `Failed to decode param '${val}'`;
        err.status = 400;
        err.statusCode = 400;
      }
      throw err;
    }
  }
}

/** Covert Route value and path to the each other */
export class RouteMatcher {
  /** @ignore */
  routeEntries: RouteEntry[];
  /** @ignore */
  routeEntryMap = { RouteEntry };
  /**
   * routeSettings: {
   *    params: any,
   *    id: string,
   *    path: string,
   *    children: any,
   *    isPrefix: boolean,
   *  }[]
   * @param {{}[]} routeSettings
   */
  constructor(
    routeSettings: {
      params: any,
      id: string,
      path: string,
      children: any,
      isPrefix: boolean,
    }[]
  ) {
    this.routeEntries = routeSettings.map(routeSetting => {
      const routeEntry = new RouteEntry(routeSetting);
      this.routeEntryMap[routeSetting.id] = routeEntry;
      return routeEntry;
    });
  }
  /**
   * Convert path string to route value
   * @return {
   *     value: any,
   *     params: any
   *  }
   */
  route(path: string): ?Route {
    for (const routeEntry of this.routeEntries) {
      const response: any = routeEntry.match(path);
      if (response) {
        if (response.childPath && routeEntry.childMatcher) {
          const childResponse = routeEntry.childMatcher.route(
            response.childPath
          );
          if (childResponse) {
            Object.assign(response.value, childResponse.value, {
              id: response.value.id + "." + childResponse.value.id,
            });
            response.params = Object.assign(
              {},
              response.params,
              childResponse.params
            );
          }
          delete response.childPath;
        }
        return response;
      }
    }
    return null;
  }
  /**
   * Convert route value to path string
   * @param {Object} param param
   * @param {string[]} ids internal use
   */
  toPath(param: any, _ids: ?(string[]) = null): string {
    const ids = _ids || param.id.split(".");
    const id = ids[0];
    if (!id) {
      return "";
    }
    const routeEntry = this.routeEntryMap[id];
    let path = routeEntry.toPath(param);
    if (ids.length > 1 && routeEntry.childMatcher) {
      path += routeEntry.childMatcher.toPath(param, ids.slice(1));
    }
    return path;
  }
}

// @flow

import EventEmitter from "events";
import stringify from "json-stable-stringify";
import { Buffer } from "buffer";

/** @abstract */
export class StateHistory {
  /** @ignore */
  shareMode = true;
  /** @ignore */
  localValue: any;
  /** @ignore */
  localData: string;
  /** @ignore */
  urlPath: string;
  /** @ignore */
  options: any;
  /** @ignore */
  path: string;
  /** @ignore */
  emitter = new EventEmitter();
  /** Constructor
   * @param {{onPathChange: function}} options options
   */
  constructor(options: {
    onPathChange: (path: string, localValue: any) => void,
  }) {
    this.options = options || {};
    if (this.options.onPathChange) {
      this.emitter.on("pathChange", this.options.onPathChange);
    }
    const currentState = this.parsePath();
    this.updatePath(currentState.path, null, {
      enableEvent: true,
      localData: currentState.localData,
    });
    window.addEventListener("popstate", this.onPopState, false);
  }
  /** Register listner
   * @param {function(path: string, localValue: any): void} listener listener
   */
  on(listener: (path: string, localValue: any) => void): void {
    this.emitter.on("pathChange", listener);
  }
  /** Remove listner
   * @param {function(path: string, localValue: any): void} listener listener
   */
  off(listener: Function) {
    this.emitter.removeListener("pathChange", listener);
  }
  /** Release all resources */
  close() {
    window.removeEventListener("popstate", this.onPopState, false);
  }
  /** Alias of history.back() */
  back() {
    history.back();
  }
  /** Alias of history.forward() */
  forward() {
    history.forward();
  }
  /** @ignore */
  parsePath() {
    throw new Error("not implemented");
  }
  /** @ignore */
  compoundPath(path: string, localData: any) {
    throw new Error("not implemented");
  }
  /** @ignore */
  onPopState = (e: any) => {
    const currentState = this.parsePath();
    this.updatePath(currentState.path, null, {
      enableEvent: true,
      localData: currentState.localData,
    });
  };
  /** @ignore */
  toData(value: any) {
    return value && Buffer.from(stringify(value)).toString("base64");
  }
  /** @ignore */
  fromData(data: string) {
    return data && JSON.parse(Buffer.from(data, "base64").toString());
  }
  /** Set the value to determine localValue is reflect to URL */
  setShareMode(shareMode: boolean) {
    this.shareMode = shareMode;
    this.updatePath(this.path, null, { localData: this.localData });
  }

  /**
   * Update inner state
   * @param {string} newPath newPath
   * @param {Object} localValue arbitrary value
   * @param {{title: string}} option options
   */
  updatePath(
    _newPath: string,
    localValue: any,
    option: { enableEvent?: boolean, localData: string, title?: string }
  ) {
    const newPath = _newPath || this.path;
    const { enableEvent, title } = option || {};
    let localData: string = (option || {}).localData;
    if (!localData && localValue) {
      localData = this.toData(localValue);
    }

    // param -> path(& state)
    const currentState = this.parsePath();
    const urlPath = this.compoundPath(newPath, localData);
    if (newPath !== currentState.path) {
      window.history.pushState({ localData: localData }, "", urlPath);
    } else if (
      localData !== currentState.localData ||
      urlPath !== this.urlPath
    ) {
      window.history.replaceState({ localData: localData }, "", urlPath);
    }
    if (title) {
      document.title = title;
    }
    // path(& state) -> inner, param
    if (this.urlPath !== urlPath) {
      this.urlPath = urlPath;
      if (this.path !== newPath) {
        this.path = newPath;
      }
      if (this.localData !== localData) {
        this.localData = localData;
        this.localValue = this.fromData(localData);
      }
      if (enableEvent) {
        this.emitter.emit("pathChange", {
          path: newPath,
          localValue: this.localValue,
        });
      }
    }
  }
  /**
   * get current state
   */
  getCurrentParam(): { path: string, localValue: any } {
    return {
      path: this.path,
      localValue: this.localValue,
    };
  }
}

/**
 * Implementation of StateHistory with location.pathname and locaiton.query
 * @example
 * // URL
 * /user/1234?eyJvcHRpb25zIjp7ImxhbmciOiJqYSJ9fQ==
 * // getCurrentParam()
 * { "path": "/user/1234", "localValue": {"lang": "en"}}
 */
export class PathStateHistory extends StateHistory {
  /** @ignore */
  parsePath() {
    return {
      path: location.pathname,
      localData:
        (location.search && location.search.slice(1)) ||
        (history.state && history.state.localData),
    };
  }
  /** @ignore */
  compoundPath(path: string, localData: any) {
    return path + (localData && this.shareMode ? "?" + localData : "");
  }
}

/**
 * Implementation of StateHistory with location.hash
 * @example
 * // URL
 * /somepath#/user/1234?eyJvcHRpb25zIjp7ImxhbmciOiJqYSJ9fQ==
 * // getCurrentParam()
 * { "path": "/user/1234", "localValue": {"lang": "en"}}
 */
class HashStateHistory extends StateHistory {
  /** @ignore */
  parsePath() {
    const path = (location.hash && location.hash.slice(1)) || "/";
    const match = path.match(/^([^\?]+)(?:\?(.*))?$/);
    return (
      (match && {
        path: match[1],
        localData: match[2] || (history.state && history.state.localData),
      }) ||
      {}
    );
  }
  /** @ignore */
  compoundPath(path: string, localData: any) {
    return (
      location.pathname +
      "#" +
      path +
      (localData && this.shareMode ? "?" + localData : "")
    );
  }
}

export { HashStateHistory };

# funcX Router

Universal routing utility for Javascript Application without dependency to any framework (including funcX).

funcX Router gives the way to convert the both of predefined path and arbitrary value to/from url and manipulate it as historical state.

```
// URL Path
#/user/1234?eyJvcHRpb25zIjp7ImxhbmciOiJqYSJ9fQ==
      or
/user/1234?eyJvcHRpb25zIjp7ImxhbmciOiJqYSJ9fQ==

// Path Parameter
{ "path": "/user/1234", "localValue": {"lang": "en"}}

// Route Parameter
{id: "user", userId: "1234"}
```

## Installation

```
npm install funcx-router
```

## Components

| Name             | Description                                                                |
| ---------------- | -------------------------------------------------------------------------- |
| StateHistory     | Register string (path) and arbitrary value (localValue) as state.          |
| HashStateHistory | Implemenetation of StateHistory with location.hash.                        |
| PathStateHistory | Implemenetation of StateHistory with location.pathname and location.query. |
| RouteMatcher     | Convert route <-> path with toPath() and toRoute() method.                 |

## Usage (StateHistory)

```js
import { HashStateHistory } from 'funcx-router';
const stateHistory = new HashStateHistory();

stateHistory.on((stateParam)=>{
  console.log("LOG: ", JSON.stringify(stateParam))  
})

stateHistory.updatePath('/user', {"options": {"lang": "en"}});
stateHistory.updatePath('/user/12345', {"options: {"lang": "ja"}});

history.back();// LOG: { "path": "/user/", "localValue": {"lang": "en"}}

console.log(history.hash); // #/user?eyJvcHRpb25zIjp7ImxhbmciOiJqYSJ9fQ==

location.hash = "#/thing?InBxciI="; // LOG: { "path": "/thing", "localValue": "pqr"}

stateHistory.setShareMode(false)

console.log(history.hash); // #/thing

// localValue is hided but still alive as an inner state.
```

## Usage (RouteMatcher)

```js
import { RouteMatcher } from "funcx-router";
const routeMatcher = new RouteMatcher([
  {
    path: "/user/:userId",
    id: "user",
    params: {
      mainComponent: "user",
      component: ActionPanel,
      schema: schema,
    },
    children: [
      {
        path: "/image/:imageId",
        id: "image",
        params: {
          subComponent: "image",
        },
      },
    ],
  },
  {
    path: "/rep/:userId*",
    id: "rep",
  },
  {
    path: "*",
    id: "all",
  },
]);

routeMatcher.route("/user/1234/image/5678");
// {value: { id: "user.image", userId: 1234, imageId: 5678}, params: {mainComponent: 'user', subComponent: 'image'}}
routeMatcher.route("/rep/1234/5678");
// {value: { id: "rep", userId: ["1234", "5678"]}}

routeMatcher.toPath({ id: "user.image", userId: 1234, imageId: 5678 });
// "/user/1234/image/5678"
```

